content:
前言 cve 官网或者工信部会发布一些 cve 漏洞，可以看到该漏洞在某次 commit 提交代码后修复的。
可以通过检索 kernel.org 中所有内核版本的 ChangeLog 文件中是否包含该 commit 来判断漏洞影响的内核版本（仅针对 linux 的 kernel 相关的漏洞）
脚本 #!/bin/bash # author: lvbibir # date: 2022-06-23 # 检索 kernel.org 下的所有 ChangeLog 文件，是否包含某项特定的 commit 号 commit=&#39;520778042ccca019f3ffa136dd0ca565c486cedd&#39; version=4 number=0 curl -ks https://cdn.kernel.org/pub/linux/kernel/v$version\.x/ &gt; list_$version cat list_$version | grep Change | grep -v sign | awk -F\&#34; &#39;{print $2}&#39; &gt; list_$version\_cut total=`wc -l list_$version\_cut | awk &#39;{print $1}&#39;` while read line; do let &#39;number+=1&#39; url=&#34;https://cdn.kernel.org/pub/linux/kernel/v$version.x/$line&#34; echo -e &#34;\033[31m---------------------正在检索$url----------------第$number 个文件，共$total 个文件\033[0m&#34; curl -ks $url | grep $commit if [ $? -eq 0 ]; then echo $url &gt;&gt; ./result_$version fi done &lt; ./list_$version\_cut echo -e &#34;\033[32m脚本执行完成，结果已保存至当前目录的 result_$version \033[0m&#34;

代码如下
#!/bin/bash #参数定义 date=`date +&#34;%Y-%m-%d-%H:%M:%S&#34;` centosVersion=$(awk &#39;{print $(NF-1)}&#39; /etc/redhat-release) VERSION=`date +%F` #日志相关 LOGPATH=&#34;/tmp/awr&#34; [ -e $LOGPATH ] || mkdir -p $LOGPATH RESULTFILE=&#34;$LOGPATH/HostCheck-`hostname`-`date +%Y%m%d`.txt&#34; #调用函数库 [ -f /etc/init.d/functions ] &amp;&amp; source /etc/init.d/functions export PATH=/usr/local/sbin:/usr/local/bin:/sbin:/bin:/usr/sbin:/usr/bin:/root/bin source /etc/profile #root用户执行脚本 [ $(id -u) -gt 0 ] &amp;&amp; echo &#34;请用root用户执行此脚本！&#34; &amp;&amp; exit 1 function version(){ echo &#34;&#34; echo &#34;&#34; echo &#34;[${date}] &gt;&gt;&gt; `hostname -s` 主机巡检&#34; } function getSystemStatus(){ echo &#34;&#34; echo -e &#34;\033[33m****************************************************系统检查****************************************************\033[0m&#34; if [ -e /etc/sysconfig/i18n ];then default_LANG=&#34;$(grep &#34;LANG=&#34; /etc/sysconfig/i18n | grep -v &#34;^#&#34; | awk -F &#39;&#34;&#39; &#39;{print $2}&#39;)&#34; else default_LANG=$LANG fi export LANG=&#34;en_US.UTF-8&#34; Release=$(cat /etc/redhat-release 2&gt;/dev/null) Kernel=$(uname -r) OS=$(uname -o) Hostname=$(uname -n) SELinux=$(/usr/sbin/sestatus | grep &#34;SELinux status: &#34; | awk &#39;{print $3}&#39;) LastReboot=$(who -b | awk &#39;{print $3,$4}&#39;) uptime=$(uptime | sed &#39;s/.*up \([^,]*\), .*/\1/&#39;) echo &#34; 系统：$OS&#34; echo &#34; 发行版本：$Release&#34; echo &#34; 内核：$Kernel&#34; echo &#34; 主机名：$Hostname&#34; echo &#34; SELinux：$SELinux&#34; echo &#34;语言/编码：$default_LANG&#34; echo &#34; 当前时间：$(date +&#39;%F %T&#39;)&#34; echo &#34; 最后启动：$LastReboot&#34; echo &#34; 运行时间：$uptime&#34; export LANG=&#34;$default_LANG&#34; } function getCpuStatus(){ echo &#34;&#34; echo -e &#34;\033[33m****************************************************CPU检查*****************************************************\033[0m&#34; Physical_CPUs=$(grep &#34;physical id&#34; /proc/cpuinfo| sort | uniq | wc -l) Virt_CPUs=$(grep &#34;processor&#34; /proc/cpuinfo | wc -l) CPU_Kernels=$(grep &#34;cores&#34; /proc/cpuinfo|uniq| awk -F &#39;: &#39; &#39;{print $2}&#39;) CPU_Type=$(grep &#34;model name&#34; /proc/cpuinfo | awk -F &#39;: &#39; &#39;{print $2}&#39; | sort | uniq) CPU_Arch=$(uname -m) echo &#34;物理CPU个数:$Physical_CPUs&#34; echo &#34;逻辑CPU个数:$Virt_CPUs&#34; echo &#34;每CPU核心数:$CPU_Kernels&#34; echo &#34; CPU型号:$CPU_Type&#34; echo &#34; CPU架构:$CPU_Arch&#34; } function getMemStatus(){ echo &#34;&#34; echo -e &#34;\033[33m**************************************************内存检查*****************************************************\033[0m&#34; if [[ $centosVersion &lt; 7 ]];then free -mo else free -h fi #报表信息 MemTotal=$(grep MemTotal /proc/meminfo| awk &#39;{print $2}&#39;) #KB MemFree=$(grep MemFree /proc/meminfo| awk &#39;{print $2}&#39;) #KB let MemUsed=MemTotal-MemFree MemPercent=$(awk &#34;BEGIN {if($MemTotal==0){printf 100}else{printf \&#34;%.2f\&#34;,$MemUsed*100/$MemTotal}}&#34;) } function getDiskStatus(){ echo &#34;&#34; echo -e &#34;\033[33m**************************************************磁盘检查******************************************************\033[0m&#34; df -hiP | sed &#39;s/Mounted on/Mounted/&#39;&gt; /tmp/inode df -hTP | sed &#39;s/Mounted on/Mounted/&#39;&gt; /tmp/disk join /tmp/disk /tmp/inode | awk &#39;{print $1,$2,&#34;|&#34;,$3,$4,$5,$6,&#34;|&#34;,$8,$9,$10,$11,&#34;|&#34;,$12}&#39;| column -t #报表信息 diskdata=$(df -TP | sed &#39;1d&#39; | awk &#39;$2!=&#34;tmpfs&#34;{print}&#39;) #KB disktotal=$(echo &#34;$diskdata&#34; | awk &#39;{total+=$3}END{print total}&#39;) #KB diskused=$(echo &#34;$diskdata&#34; | awk &#39;{total+=$4}END{print total}&#39;) #KB diskfree=$((disktotal-diskused)) #KB diskusedpercent=$(echo $disktotal $diskused | awk &#39;{if($1==0){printf 100}else{printf &#34;%.2f&#34;,$2*100/$1}}&#39;) inodedata=$(df -iTP | sed &#39;1d&#39; | awk &#39;$2!=&#34;tmpfs&#34;{print}&#39;) inodetotal=$(echo &#34;$inodedata&#34; | awk &#39;{total+=$3}END{print total}&#39;) inodeused=$(echo &#34;$inodedata&#34; | awk &#39;{total+=$4}END{print total}&#39;) inodefree=$((inodetotal-inodeused)) inodeusedpercent=$(echo $inodetotal $inodeused | awk &#39;{if($1==0){printf 100}else{printf &#34;%.2f&#34;,$2*100/$1}}&#39;) } function get_resource(){ echo &#34;&#34; echo -e &#34;\033[33m**************************************************资源消耗统计**************************************************\033[0m&#34; echo -e &#34;\033[36m*************带宽资源消耗统计*************\033[0m&#34; #用数组存放网卡名 nic=(`ifconfig | grep ^[a-z] | grep -vE &#39;lo|docker0&#39;| awk -F: &#39;{print $1}&#39;`) time=`date &#34;+%Y-%m-%d %k:%M&#34;` num=0 for ((i=0;i&lt;${#nic[@]};i++)) do #循环五次，避免看到的是偶然的数据 while (( $num&lt;5 )) do rx_before=$(cat /proc/net/dev | grep &#39;${nic[$i]}&#39; | tr : &#34; &#34; | awk &#39;{print $2}&#39;) tx_before=$(cat /proc/net/dev | grep &#39;${nic[$i]}&#39; | tr : &#34; &#34; | awk &#39;{print $10}&#39;) sleep 2 #用sed先获取第7列,再用awk获取第2列，再cut切割,从第7个到最后，即只切割网卡流量数字部分 rx_after=$(cat /proc/net/dev | grep &#39;${nic[$i]}&#39; | tr : &#34; &#34; | awk &#39;{print $2}&#39;) tx_after=$(cat /proc/net/dev | grep &#39;${nic[$i]}&#39; | tr : &#34; &#34; | awk &#39;{print $10}&#39;) #注意下面截取的相差2秒的两个时刻的累计和发送的bytes(即累计传送和接收的位) rx_result=$[(rx_after-rx_before)/1024/1024/2*8] tx_result=$[(tx_after-tx_before)/1024/1024/2*8] echo &#34;$time Now_In_Speed: $rx_result Mbps Now_OUt_Speed: $tx_result Mbps&#34; &gt;&gt; /tmp/network.txt let &#34;num++&#34; done #注意下面grep后面的$time变量要用双引号括起来 rx_result=$(cat /tmp/network.txt|grep &#34;$time&#34;|awk &#39;{In+=$4}END{print In}&#39;) tx_result=$(cat /tmp/network.txt|grep &#34;$time&#34;|awk &#39;{Out+=$7}END{print Out}&#39;) In_Speed=$(echo &#34;scale=2;$rx_result/5&#34;|bc) Out_Speed=$(echo &#34;scale=2;$tx_result/5&#34;|bc) echo -e &#34;\033[32m In_Speed_average: $In_Speed Mbps Out_Speed_average: $Out_Speed Mbps! \033[0m&#34; done echo -e &#34;\033[36m*************CPU资源消耗统计*************\033[0m&#34; #使用vmstat 1 5命令统计5秒内的使用情况，再计算每秒使用情况 total=`vmstat 1 5|awk &#39;{x+=$13;y+=$14}END{print x+y}&#39;` cpu_average=$(echo &#34;scale=2;$total/5&#34;|bc) #判断CPU使用率（浮点数与整数比较） if [ `echo &#34;${cpu_average} &gt; 70&#34; | bc` -eq 1 ];then echo -e &#34;\033[31m Total CPU is already use: ${cpu_average}%,请及时处理！\033[0m&#34; else echo -e &#34;\033[32m Total CPU is already use: ${cpu_average}%! \033[0m&#34; fi echo -e &#34;\033[36m*************磁盘资源消耗统计*************\033[0m&#34; #磁盘使用情况(注意：需要用sed先进行格式化才能进行累加处理) disk_used=$(df -m | sed &#39;1d;/ /!N;s/
//;s/ \+/ /;&#39; | awk &#39;{used+=$3} END{print used}&#39;) disk_totalSpace=$(df -m | sed &#39;1d;/ /!N;s/
//;s/ \+/ /;&#39; | awk &#39;{totalSpace+=$2} END{print totalSpace}&#39;) disk_all=$(echo &#34;scale=4;$disk_used/$disk_totalSpace&#34; | bc) disk_percent1=$(echo $disk_all | cut -c 2-3) disk_percent2=$(echo $disk_all | cut -c 4-5) disk_warning=`df -m | sed &#39;1d;/ /!N;s/
//;s/ \+/ /;&#39; | awk &#39;{if ($5&gt;85) print $6 &#34;目录使用率：&#34; $5;} &#39;` echo -e &#34;\033[32m Total disk has used: $disk_percent1.$disk_percent2% \033[0m&#34; #echo -e &#34;		..&#34; 表示换行 if [ -n &#34;$disk_warning&#34; ];then echo -e &#34;\033[31m${disk_warning} 
 [Error]以上目录使用率超过85%，请及时处理！\033[0m&#34; fi echo -e &#34;\033[36m*************内存资源消耗统计*************\033[0m&#34; #获得系统总内存 memery_all=$(free -m | awk &#39;NR==2&#39; | awk &#39;{print $2}&#39;) #获得占用内存（操作系统 角度） system_memery_used=$(free -m | awk &#39;NR==2&#39; | awk &#39;{print $3}&#39;) #获得buffer、cache占用内存，当内存不够时会及时回收，所以这两部分可用于可用内存的计算 buffer_used=$(free -m | awk &#39;NR==2&#39; | awk &#39;{print $6}&#39;) cache_used=$(free -m | awk &#39;NR==2&#39; | awk &#39;{print $7}&#39;) #获得被使用内存，所以这部分可用于可用内存的计算，注意计算方法 actual_used_all=$[memery_all-(free+buffer_used+cache_used)] #获得实际占用的内存 actual_used_all=`expr $memery_all - $free + $buffer_used + $cache_used ` memery_percent=$(echo &#34;scale=4;$system_memery_used / $memery_all&#34; | bc) memery_percent2=$(echo &#34;scale=4; $actual_used_all / $memery_all&#34; | bc) percent_part1=$(echo $memery_percent | cut -c 2-3) percent_part2=$(echo $memery_percent | cut -c 4-5) percent_part11=$(echo $memery_percent2 | cut -c 2-3) percent_part22=$(echo $memery_percent2 | cut -c 4-5) #获得占用内存（操作系统角度） echo -e &#34;\033[32m system memery is already use: $percent_part1.$percent_part2% \033[0m&#34; #获得实际内存占用率 echo -e &#34;\033[32m actual memery is already use: $percent_part11.$percent_part22% \033[0m&#34; echo -e &#34;\033[32m buffer is already used : $buffer_used M \033[0m&#34; echo -e &#34;\033[32m cache is already used : $cache_used M \033[0m&#34; } function getServiceStatus(){ echo &#34;&#34; echo -e &#34;\033[33m*************************************************服务检查*******************************************************\033[0m&#34; echo &#34;&#34; if [[ $centosVersion &gt; 7 ]];then conf=$(systemctl list-unit-files --type=service --state=enabled --no-pager | grep &#34;enabled&#34;) process=$(systemctl list-units --type=service --state=running --no-pager | grep &#34;.service&#34;) else conf=$(/sbin/chkconfig | grep -E &#34;:on|:启用&#34;) process=$(/sbin/service --status-all 2&gt;/dev/null | grep -E &#34;is running|正在运行&#34;) fi echo -e &#34;\033[36m******************服务配置******************\033[0m&#34; echo &#34;$conf&#34; | column -t echo &#34;&#34; echo -e &#34;\033[36m**************正在运行的服务****************\033[0m&#34; echo &#34;$process&#34; } function getAutoStartStatus(){ echo &#34;&#34; echo -e &#34;\033[33m***********************************************自启动检查*******************************************************\033[0m&#34; echo -e &#34;\033[36m****************自启动命令*****************\033[0m&#34; conf=$(grep -v &#34;^#&#34; /etc/rc.d/rc.local| sed &#39;/^$/d&#39;) echo &#34;$conf&#34; } function getLoginStatus(){ echo &#34;&#34; echo -e &#34;\033[33m************************************************登录检查********************************************************\033[0m&#34; last | head } function getNetworkStatus(){ echo &#34;&#34; echo -e &#34;\033[33m************************************************网络检查********************************************************\033[0m&#34; if [[ $centosVersion &lt; 7 ]];then /sbin/ifconfig -a | grep -v packets | grep -v collisions | grep -v i net6 else #ip a for i in $(ip link | grep BROADCAST | awk -F: &#39;{print $2}&#39;);do ip add show $i | grep -E &#34;BROADCAST|global&#34;| awk &#39;{print $2}&#39; | tr &#39;
&#39; &#39; &#39; ;echo &#34;&#34; ;done fi GATEWAY=$(ip route | grep default | awk &#39;{print $3}&#39;) DNS=$(grep nameserver /etc/resolv.conf| grep -v &#34;#&#34; | awk &#39;{print $2}&#39; | tr &#39;
&#39; &#39;,&#39; | sed &#39;s/,$//&#39;) echo &#34;&#34; echo &#34;网关：$GATEWAY &#34; echo &#34;DNS：$DNS&#34; #报表信息 IP=$(ip -f inet addr | grep -v 127.0.0.1 | grep inet | awk &#39;{print $NF,$2}&#39; | tr &#39;
&#39; &#39;,&#39; | sed &#39;s/,$//&#39;) MAC=$(ip link | grep -v &#34;LOOPBACK\|loopback&#34; | awk &#39;{print $2}&#39; | sed &#39;N;s/
//&#39; | tr &#39;
&#39; &#39;,&#39; | sed &#39;s/,$//&#39;) echo &#34;&#34; ping -c 4 www.baidu.com &gt;/dev/null 2&gt;&amp;1 if [ $? -eq 0 ];then echo &#34;&#34; echo -e &#34;\033[32m网络连接：正常！\033[0m&#34; else echo &#34;&#34; echo -e &#34;\033[31m网络连接：异常！\033[0m&#34; fi } function getListenStatus(){ echo &#34;&#34; echo -e &#34;\033[33m***********************************************监听检查********************************************************\033[0m&#34; TCPListen=$(ss -ntul | column -t) echo &#34;$TCPListen&#34; } function getCronStatus(){ echo &#34;&#34; echo -e &#34;\033[33m**********************************************计划任务检查******************************************************\033[0m&#34; Crontab=0 for shell in $(grep -v &#34;/sbin/nologin&#34; /etc/shells);do for user in $(grep &#34;$shell&#34; /etc/passwd| awk -F: &#39;{print $1}&#39;);do crontab -l -u $user &gt;/dev/null 2&gt;&amp;1 status=$? if [ $status -eq 0 ];then echo -e &#34;\033[36m************$user用户的定时任务**************\033[0m&#34; crontab -l -u $user let Crontab=Crontab+$(crontab -l -u $user | wc -l) echo &#34;&#34; fi done done #计划任务 #find /etc/cron* -type f | xargs -i ls -l {} | column -t #let Crontab=Crontab+$(find /etc/cron* -type f | wc -l) } function getHowLongAgo(){ # 计算一个时间戳离现在有多久了 datetime=&#34;$*&#34; [ -z &#34;$datetime&#34; ] &amp;&amp; echo `stat /etc/passwd|awk &#34;NR==6&#34;` Timestamp=$(date +%s -d &#34;$datetime&#34;) Now_Timestamp=$(date +%s) Difference_Timestamp=$(($Now_Timestamp-$Timestamp)) days=0;hours=0;minutes=0; sec_in_day=$((60*60*24)); sec_in_hour=$((60*60)); sec_in_minute=60 while (( $(($Difference_Timestamp-$sec_in_day)) &gt; 1 )) do let Difference_Timestamp=Difference_Timestamp-sec_in_day let days++ done while (( $(($Difference_Timestamp-$sec_in_hour)) &gt; 1 )) do let Difference_Timestamp=Difference_Timestamp-sec_in_hour let hours++ done echo &#34;$days 天 $hours 小时前&#34; } function getUserLastLogin(){ # 获取用户最近一次登录的时间，含年份 # 很遗憾last命令不支持显示年份，只有&#34;last -t YYYYMMDDHHMMSS&#34;表示某个时间之间的登录，我 # 们只能用最笨的方法了，对比今天之前和今年元旦之前（或者去年之前和前年之前……）某个用户 # 登录次数，如果登录统计次数有变化，则说明最近一次登录是今年。 username=$1 : ${username:=&#34;`whoami`&#34;} thisYear=$(date +%Y) oldesYear=$(last | tail -n1 | awk &#39;{print $NF}&#39;) while(( $thisYear &gt;= $oldesYear));do loginBeforeToday=$(last $username | grep $username | wc -l) loginBeforeNewYearsDayOfThisYear=$(last $username -t $thisYear&#34;0101000000&#34; | grep $username | wc -l) if [ $loginBeforeToday -eq 0 ];then echo &#34;从未登录过&#34; break elif [ $loginBeforeToday -gt $loginBeforeNewYearsDayOfThisYear ];then lastDateTime=$(last -i $username | head -n1 | awk &#39;{for(i=4;i&lt;(NF-2);i++)printf&#34;%s &#34;,$i}&#39;)&#34; $thisYear&#34; lastDateTime=$(date &#34;+%Y-%m-%d %H:%M:%S&#34; -d &#34;$lastDateTime&#34;) echo &#34;$lastDateTime&#34; break else thisYear=$((thisYear-1)) fi done } function getUserStatus(){ echo &#34;&#34; echo -e &#34;\033[33m*************************************************用户检查*******************************************************\033[0m&#34; #/etc/passwd 最后修改时间 pwdfile=&#34;$(cat /etc/passwd)&#34; Modify=$(stat /etc/passwd | grep Modify | tr &#39;.&#39; &#39; &#39; | awk &#39;{print $2,$3}&#39;) echo &#34;/etc/passwd: $Modify ($(getHowLongAgo $Modify))&#34; echo &#34;&#34; echo -e &#34;\033[36m******************特权用户******************\033[0m&#34; RootUser=&#34;&#34; for user in $(echo &#34;$pwdfile&#34; | awk -F: &#39;{print $1}&#39;);do if [ $(id -u $user) -eq 0 ];then echo &#34;$user&#34; RootUser=&#34;$RootUser,$user&#34; fi done echo &#34;&#34; echo -e &#34;\033[36m******************用户列表******************\033[0m&#34; USERs=0 echo &#34;$( echo &#34;用户名 UID GID HOME SHELL 最后一次登录&#34; for shell in $(grep -v &#34;/sbin/nologin&#34; /etc/shells);do for username in $(grep &#34;$shell&#34; /etc/passwd| awk -F: &#39;{print $1}&#39;);do userLastLogin=&#34;$(getUserLastLogin $username)&#34; echo &#34;$pwdfile&#34; | grep -w &#34;$username&#34; |grep -w &#34;$shell&#34;| awk -F: -v lastlogin=&#34;$(echo &#34;$userLastLogin&#34; | tr &#39; &#39; &#39;_&#39;)&#34; &#39;{print $1,$3,$4,$6,$7,lastlogin}&#39; done let USERs=USERs+$(echo &#34;$pwdfile&#34; | grep &#34;$shell&#34;| wc -l) done )&#34; | column -t echo &#34;&#34; echo -e &#34;\033[36m******************空密码用户****************\033[0m&#34; USEREmptyPassword=&#34;&#34; for shell in $(grep -v &#34;/sbin/nologin&#34; /etc/shells);do for user in $(echo &#34;$pwdfile&#34; | grep &#34;$shell&#34; | cut -d: -f1);do r=$(awk -F: &#39;$2==&#34;!!&#34;{print $1}&#39; /etc/shadow | grep -w $user) if [ ! -z $r ];then echo $r USEREmptyPassword=&#34;$USEREmptyPassword,&#34;$r fi done done echo &#34;&#34; echo -e &#34;\033[36m*****************相同ID用户*****************\033[0m&#34; USERTheSameUID=&#34;&#34; UIDs=$(cut -d: -f3 /etc/passwd | sort | uniq -c | awk &#39;$1&gt;1{print $2}&#39;) for uid in $UIDs;do echo -n &#34;$uid&#34;; USERTheSameUID=&#34;$uid&#34; r=$(awk -F: &#39;ORS=&#34;&#34;;$3==&#39;&#34;$uid&#34;&#39;{print &#34;:&#34;,$1}&#39; /etc/passwd) echo &#34;$r&#34; echo &#34;&#34; USERTheSameUID=&#34;$USERTheSameUID $r,&#34; done } function getPasswordStatus { echo &#34;&#34; echo -e &#34;\033[33m*************************************************密码检查*******************************************************\033[0m&#34; pwdfile=&#34;$(cat /etc/passwd)&#34; echo &#34;&#34; echo -e &#34;\033[36m****************密码过期检查****************\033[0m&#34; result=&#34;&#34; for shell in $(grep -v &#34;/sbin/nologin&#34; /etc/shells);do for user in $(echo &#34;$pwdfile&#34; | grep &#34;$shell&#34; | cut -d: -f1);do get_expiry_date=$(/usr/bin/chage -l $user | grep &#39;Password expires&#39; | cut -d: -f2) if [[ $get_expiry_date = &#39; never&#39; || $get_expiry_date = &#39;never&#39; ]];then printf &#34;%-15s 永不过期
&#34; $user result=&#34;$result,$user:never&#34; else password_expiry_date=$(date -d &#34;$get_expiry_date&#34; &#34;+%s&#34;) current_date=$(date &#34;+%s&#34;) diff=$(($password_expiry_date-$current_date)) let DAYS=$(($diff/(60*60*24))) printf &#34;%-15s %s天后过期
