 grep -v &#34;^#&#34; | sed &#39;/^$/d&#39; fi } function getState(){ if [[ $centosVersion &lt; 7 ]];then if [ -e &#34;/etc/init.d/$1&#34; ];then if [ `/etc/init.d/$1 status 2&gt;/dev/null | grep -E &#34;is running|正在运行&#34; | wc -l` -ge 1 ];then r=&#34;active&#34; else r=&#34;inactive&#34; fi else r=&#34;unknown&#34; fi else #CentOS 7+ r=&#34;$(systemctl is-active $1 2&gt;&amp;1)&#34; fi echo &#34;$r&#34; } function getSSHStatus(){ #SSHD服务状态，配置,受信任主机等 echo &#34;&#34; echo -e &#34;\033[33m************************************************SSH检查*********************************************************\033[0m&#34; #检查受信任主机 pwdfile=&#34;$(cat /etc/passwd)&#34; echo &#34;SSH服务状态：$(getState sshd)&#34; Protocol_Version=$(cat /etc/ssh/sshd_config | grep Protocol | awk &#39;{print $2}&#39;) echo &#34;SSH协议版本：$Protocol_Version&#34; echo &#34;&#34; echo -e &#34;\033[36m****************信任主机******************\033[0m&#34; authorized=0 for user in $(echo &#34;$pwdfile&#34; | grep /bin/bash | awk -F: &#39;{print $1}&#39;);do authorize_file=$(echo &#34;$pwdfile&#34; | grep -w $user | awk -F: &#39;{printf $6&#34;/.ssh/authorized_keys&#34;}&#39;) authorized_host=$(cat $authorize_file 2&gt;/dev/null | awk &#39;{print $3}&#39; | tr &#39;
